    /**
     * 解密
     * @throws IOException
     */
    @RequestMapping(value = "/t3")
    @ResponseBody
    public String t3() throws IOException {
        CloseableHttpClient httpclient = HttpClients.createDefault();
        HttpGet httpget = new HttpGet("http://localhost:8080/t2");
        CloseableHttpResponse response = httpclient.execute(httpget);
        HttpEntity entity = response.getEntity();
        if (entity != null) {
            try (InputStream inputStream = entity.getContent()) {

                byte[] en = IOUtils.toByteArray(inputStream);
                byte[] b = MyAESUtils.decrypt(en, KEY);

                String json = new String(b, "utf-8");
                MddDataResp mdd = JSON.parseObject(json, MddDataResp.class);
                return JSON.toJSONString(mdd);

            }
        }
        return "";
    }


    @PostMapping(value = "/t4")
    @ResponseBody
    public ResponseEntity<byte[]> t4(@RequestBody MddOrderStateReq mddOrderStateReq) throws UnsupportedEncodingException {
        List<MddOrderState> list = new ArrayList<>();
        for(int i=0;i<5;i++){
            MddOrderState mddOrderState = new MddOrderState();
            mddOrderState.setOrderSn("JKJ"+i);
            mddOrderState.setOrderState(i);
            list.add(mddOrderState);
        }
        MddOrderStateResp mddOrderStateResp = new MddOrderStateResp();
        mddOrderStateResp.setStatusCode(200);
        mddOrderStateResp.setSuccess(true);
        mddOrderStateResp.setMsg("成功");
        mddOrderStateResp.setMsgEN("success");
        mddOrderStateResp.setData(list);

        HttpHeaders headers = new HttpHeaders();
        headers.setContentType(MediaType.APPLICATION_OCTET_STREAM);
        String json = JSON.toJSONString(mddOrderStateResp);
        byte[] text = json.getBytes("utf-8");
        byte[] en = MyAESUtils.encrypt(json, KEY);

        return new ResponseEntity<byte[]>(en, headers, HttpStatus.OK);
    }

